if(Sys_User!=null)
{
   if( Sys_User.Name!=null){
	Screen.EmployeeCode=Sys_User.Name;
	 }
    if( Sys_User.EmployeeInfo!=null){
	Screen.EmpolyeeName=Sys_User.EmployeeInfo.EmployeeCHName;
	 }
}

var GetNum=0;
var  GetNumTrue=0;
foreach(var item in Items)
{ 
      Screen.check=true;
      	if(item.工号==null)
 	{
 		MessageBox.Show('第'+ (GetNum+1).ToString()+'行工号为空！');
	}
	else
	{
		var employee=ApplicationData.T_EmployeeInfoSet.Where(w=>w.PointCode==Contexts!CurrentPoint.PointCode&&w.EmployeeCode==item.工号).SingleOrDefault();
		//判断工号是否存在
 		if(employee!=null)
	 	{
	 		MessageBox.Show('第'+ (GetNum+1).ToString()+'条姓名：'+item.姓名.ToString()+'的工号已经存在！');
	 		GetNum=GetNum+1;
		}
		else
		{
	 		var Position =ApplicationData.T_PositionInfoSet.Where(w=>w.PointCode==Contexts!CurrentPoint.PointCode&& w.Position==item.职务).FirstOrDefault();
		 	if(Position==null)
		 	{
		 		MessageBox.Show('姓名：'+item.姓名.ToString()+'的职务，不存在！');
	 			Screen.check=false;
	 			break;
			}
			var Department =ApplicationData.T_DepartmentInfoSet.Where(w=>w.PointCode==Contexts!CurrentPoint.PointCode&& w.DepartmentName==item.部门).FirstOrDefault();
		 	if(Department==null)
		 	{
		 		MessageBox.Show('姓名：'+item.姓名.ToString()+'的部门，不存在！');
	 			Screen.check=false;
	 			break;
			}
			//插入员工信息
			var EmployeeInfo= new T_EmployeeInfo();
			var PointInfo=ApplicationData.T_PointInfoSet.Where(w=>w.PointCode==Contexts!CurrentPoint.PointCode).SingleOrDefault();
			EmployeeInfo.PointInfo=PointInfo;
			EmployeeInfo.DepartmentInfo=Department;
			EmployeeInfo.PositionInfo=Position;
			EmployeeInfo.EmployeeCode=item.工号;
			EmployeeInfo.EmployeeCHName=item.姓名;
			EmployeeInfo.EmployeeMobile=item.手机号码;
			EmployeeInfo.EmployeeMobile2=item.紧急联系电话;
			EmployeeInfo.EmployeeIDCardNo=item.身份证号码;
			EmployeeInfo.EmployeeBirthday = item.出生日期;
			EmployeeInfo.EmployeeHomeAddress=item.家庭住址;
			EmployeeInfo.EmployeeDescription=item.备注;
			EmployeeInfo.Status=1;
			EmployeeInfo.EmployeeRegisterDate=item.入职时间;
			
		   	GetNumTrue=GetNumTrue+1;
		   	GetNum=GetNum+1;
		}
	}
	SaveChanges();
}
MessageBox.Show('成功导入'+GetNumTrue.ToString()+'条数据 ！');
